mapper.select:
select id,project_name as projectName,project_type as projectType,applicant,applicant_id as applicantId,funds,remaining_funds as remainingFunds,
       content,start_time as startTime,deadline,update_time as updateTime,
       status,reason from tb_project_info where  status=3  and applicant_id = #{id}

select id,project_name as projectName,project_type as projectType,applicant,applicant_id as applicantId,funds,remaining_funds as remainingFunds,
       content,start_time as startTime,deadline,update_time as updateTime,
       status,reason from tb_project_info where  status in(4,5)  and applicant_id = #{id}

select id,project_name as projectName,project_type as projectType,applicant,applicant_id as applicantId,funds,remaining_funds as remainingFunds,
       content,start_time as startTime,deadline,update_time as updateTime,
       status,reason from tb_project_info where  status in (0, 1)  and applicant_id = #{id}

select id,project_name as projectName,project_type as projectType,applicant,applicant_id as applicantId,funds,remaining_funds as remainingFunds,
       content,start_time as startTime,deadline,update_time as updateTime,
       status,reason from tb_project_info where  id= #{id}
